package de.beyondjava.dominio.dao.repository;import org.springframework.data.domain.*;import org.springframework.data.repository.*;import de.beyondjava.dominio.modelo.Localidad;import de.beyondjava.dominio.modelo.Provincia;import java.util.List;public interface LocalidadRepository extends Repository<Localidad, Long> {	Page<Localidad> findAll(Pageable pageable);	Localidad findByNombreAllIgnoringCase(String nombre);	List<Localidad> findByProvincia(Provincia provincia);}