package de.beyondjava.dominio.servicio.impl;import de.beyondjava.dominio.modelo.Provincia;import de.beyondjava.dominio.servicio.ServicioProvincia;import de.beyondjava.dominio.dao.repository.ProvinciaRepository;import org.springframework.data.domain.*;import org.springframework.data.repository.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Service("servicioProvincia")public class ServicioProvinciaImpl implements ServicioProvincia {		private final ProvinciaRepository provinciaRepository;		@Autowired	public ServicioProvinciaImpl(ProvinciaRepository provinciaRepository){		this.provinciaRepository = provinciaRepository;	}		public void guardar(Provincia provincia){		provinciaRepository.save(provincia);	}		public Page<Provincia> findAll(Pageable pageable){		return provinciaRepository.findAll(pageable);	}		public Provincia findByNombreAllIgnoringCase(String nombre){		return provinciaRepository.findByNombreAllIgnoringCase(nombre);	}}